    public int subarraysDivByK(int[] A, int K) {
        Map<Integer,Integer> a = new HashMap<>();
        a.put(0,1);
        int sum =0;
        int total = 0;
        for (int i : A) {
            sum += i;
            int r = (sum % K + K) % K;
            int c = a.getOrDefault(r,0);
            total += c;
            a.put(r,c+1);
        }
        return total;
    }


