     public int subarraysDivByK2(int[] A, int K) {
         Map<String,Integer> map = new HashMap<>();
         int count =0;
         int len = A.length;
         for (int i =0;i< len;i++){
             for (int j = i; j < len; j++) {
                 int sum =0;
                 if (j-1 >=i){
                     sum += map.get(i+","+(j-1)) + A[j];
                     map.put(i+","+j,sum);
                 } else{
                     sum += A[i];
                     map.put(i+","+i,sum);
                 }
                 if (sum % K ==0){
                     count++;
                 }
             }
         }
         return count;
     }

